import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';

function ExcerciseDetails() {
  const [excerciseDetails, setExcerciseDetails] = useState([]);

  const getExcerciseDetails = async () =>{
    try{
  const response = await axios.get(`http://localhost:5005/api/excercises`);
      setExcerciseDetails(response.data);
      console.log(response.data);
}catch (error){
console.log(error);
}
};

useEffect(() =>{
  getExcerciseDetails();
}, []);

  return (
    <div className='ExcersiseDetails'>
    <h1>Exercise Details</h1>
    {excerciseDetails.map((excersise) =>{
      return(
        <div key={excersise._id} className="ExcersiseCardDetails">
        <Link to={`//excercises/details/:excerciseName${excersise._id}`}>
          <h3>{excersise.name}</h3>
        </Link>
        <h4>{excersise.type}</h4>
        <h4>{excersise.muscle}</h4>
        <h4>{excersise.equipment}</h4>
        <h4>{excersise.difficulty}</h4>
        <h4>{excersise.instructions}</h4>
        </div>
      );
    })}

    </div>
  );
}

export default ExcerciseDetails
